    {id: 'plan_btn_b2', value: 'B2'},
    {id: 'plan_btn_c', value: 'C'},
    {id: 'plan_btn_d', value: 'D'}
];
var trackControlNodeIds = [
    'track_selector_1',
    'load_track_button_1',
    'track_selector_2',
    'load_track_button_2',
    'track_selector_3',
    'load_track_button_3'
];

// Find which button was pressed
var selectedPlan = plans.find(p => p.value === msg.payload);
if (selectedPlan) {
    flow.set('currentPlan', selectedPlan.value);

    // Output 1: The main command message to change the plan
    var planMsg = {payload: selectedPlan.value};

    // Outputs 2-8: Messages to update the plan button highlights
    var uiControlMsgs = plans.map(p => ({
        ui_control: {
            bgColor: (p.id === selectedPlan.id) ? 'deepskyblue' : 'lightgray',
            color: (p.id === selectedPlan.id) ? 'white' : 'black'
        },
        topic: p.id // Targets the button by its ID
    }));

    // Outputs 9-14: Messages to enable/disable the track selection UI
    var isPlanD = (selectedPlan.value === 'D');
    var trackSelectorControls = trackControlNodeIds.map(id => ({
        ui_control: { enabled: isPlanD },
        topic: id // Targets the widget by its ID
    }));

    // Return messages on their designated outputs
    return [planMsg, ...uiControlMsgs, ...trackSelectorControls];
}

return null;

